INSERT INTO `indicator` VALUES (1,'1',NULL,55,'Initiate work on a pan-European VAT clearing house. It should be online, effective and easy to use. And it should pass the SME test easily',NULL,1,'ec',NULL,1,NULL),(2,'1',NULL,55,'Create an effective legal base for a European VAT clearing house to enable cross-border declarations and recovery ',NULL,1,'country',NULL,1,NULL),(3,'1',NULL,55,'Endorse, accept and quickly take up the new cross-border system',NULL,1,'startup',NULL,1,NULL),(4,'1',NULL,55,'The new system should be developed through PPP based on consortia and competitive tenders',NULL,1,'ecosys',NULL,1,NULL),(5,'2',NULL,56,'Enforce the usage of the SME Test in EU legislative process \n',NULL,1,'ec',NULL,1,NULL),(6,'3',NULL,56,'Develop an adapted methodology for scale-ups',NULL,1,'ec',NULL,1,NULL),(7,'2',NULL,56,'Enforce usage of SME Test mechanisms in the national legislative process ',NULL,1,'country',NULL,1,NULL),(8,'2',NULL,56,'Participate systematically in the legislative consultations under the think small first principle',NULL,1,'startup',NULL,1,NULL),(9,'2',NULL,56,'Private-sector participants are politely invited to “think small first” as well',NULL,1,'ecosys',NULL,1,NULL),(10,'4',NULL,57,'Widen and harmonise exceptions and limitations in copyright with innovation-driven businesses in mind; include inventors of new business models in impact assessment and structured dialogue',NULL,1,'ec',NULL,1,NULL),(11,'4',NULL,57,'Widen and harmonise exceptions and limitations in copyright with innovation-driven businesses in mind; include inventors of new business models in impact assessment and structured dialogue',NULL,1,'country',NULL,1,NULL),(12,'4',NULL,57,'Recognise “fair use” in copyrighted material by other entities',NULL,1,'startup',NULL,1,NULL),(13,'4',NULL,57,'Recognise “fair use” in copyrighted material by other entities',NULL,1,'ecosys',NULL,1,NULL),(14,'5',NULL,58,'Adopt a new text and data mining exception, commercial and non-commercial, when content has been rightfully obtained',NULL,1,'ec',NULL,1,NULL),(15,'5',NULL,58,'Introduce text and data mining exception, commercial and non-commercial use, when content has been rightfully obtained',NULL,1,'country',NULL,1,NULL),(16,'5',NULL,58,'Use text and data mining to build, better, stronger, more customer-driven businesses',NULL,1,'startup',NULL,1,NULL),(17,'5',NULL,58,'Use text and data mining to build, better, stronger, more customer-driven businesses',NULL,1,'ecosys',NULL,1,NULL),(18,'6',NULL,59,'Introduce a European regulation creating a single European copyright licence',NULL,1,'ec',NULL,1,NULL),(19,'6',NULL,59,'Ensure national recognition and enforcement of pan-European copyright licences',NULL,1,'country',NULL,1,NULL),(20,'6',NULL,59,'Use the licenced content to build great, innovative, cross-border businesses',NULL,1,'startup',NULL,1,NULL),(21,'6',NULL,59,'Use the licenced content to build great, innovative, cross-border businesses',NULL,1,'ecosys',NULL,1,NULL),(22,'7',NULL,60,'Restore coherence at the European level in the data-protection field',NULL,1,'ec',NULL,1,NULL),(23,'7',NULL,60,'Limit use of so-called “opening clauses” in GDPR to preserve a single ruleset',NULL,1,'country',NULL,1,NULL),(24,'7',NULL,60,'Build a climate of trust around data',NULL,1,'startup',NULL,1,NULL),(25,'8',NULL,61,'Initiate a structured dialogue with SMEs, startups and scale-ups on the effect of data rulings on small entities and cross-border businesses. The dialogue should be supplemented with impact assessments and evidence',NULL,1,'ec',NULL,1,NULL),(26,'8',NULL,61,'Data protection regulators to establish a structured dialogue with the startup community ',NULL,1,'country',NULL,1,NULL),(27,'8',NULL,61,'Participate in local startup/data protection supervisor dialogue constructively',NULL,1,'startup',NULL,1,NULL),(28,'8',NULL,61,' Associations initiate dialogue and ensure exchange of best practise',NULL,1,'ecosys',NULL,1,NULL),(29,'9',NULL,62,'Drop all data localisation requirements',NULL,1,'ec',NULL,1,NULL),(30,'9',NULL,62,'Remove national storage requirements',NULL,1,'country',NULL,1,NULL),(31,'10',NULL,63,'Work to keep transatlantic data flows secure and open and to make the new privacy shield a success',NULL,1,'ec',NULL,1,NULL),(32,'10',NULL,63,'Encourage and support full use of improved privacy shield.',NULL,1,'country',NULL,1,NULL),(33,'10',NULL,63,'Sign up and fully implement provisions of improved privacy shield where necessary',NULL,1,'startup',NULL,1,NULL),(34,'10',NULL,63,'Companies to use and promote privacy shield and respect its provisions',NULL,1,'ecosys',NULL,1,NULL),(35,'11',NULL,64,'Encourage e-ID interoperability standards adoption today; don’t let others stall\n',NULL,1,'ec',NULL,1,NULL),(36,'11',NULL,64,'Ensure that the system of eID EU interoperable by following EU technical specifications for eIDAS interoperability correctly\n',NULL,1,'country',NULL,1,NULL),(37,'11',NULL,64,'Adopt and encourage the use of robust e-Identity protocols including eID',NULL,1,'startup',NULL,1,NULL),(38,'12',NULL,65,'Offer the e-IDAS standards to the private sector for the development of robust, pan-European authentication systems',NULL,1,'ec',NULL,1,NULL),(39,'12',NULL,64,'Recognise ID from other MSs and ensured its online services accept your citizens’',NULL,1,'country',NULL,1,NULL),(40,'12',NULL,64,'Use emerging e-Identity standards to facilitate and expand cross-border commerce',NULL,1,'startup',NULL,1,NULL),(41,'12',NULL,64,' Lead by example and adopt eID technologies ',NULL,1,'ecosys',NULL,1,NULL),(42,'14',NULL,66,'Ensure that the Unified Patent Court starts to function\n',NULL,1,'ec',NULL,1,NULL),(43,'13',NULL,66,'Provide adequate information for startups on Unitary Patent Protection',NULL,1,'country',NULL,1,NULL),(44,'13',NULL,66,'Ensure uptake of unitary patent by startups and scale-ups',NULL,1,'startup',NULL,1,NULL),(45,'14',NULL,66,'Ratify Agreement on Unitary Patent Court (at least 13 member states)',NULL,1,'country',NULL,1,NULL),(46,'16',NULL,68,'Adopt a “Digital by default” principle ',NULL,1,'ec',NULL,1,NULL),(47,'16',NULL,68,'Adopt a “Digital by default” principle ',NULL,1,'country',NULL,1,NULL),(48,'15',NULL,67,'Publish all official documents online in certified translations (multi-language)',NULL,1,'country',NULL,1,NULL),(49,'16',NULL,68,'Ensure uptake of online government by startups and scaleups',NULL,1,'startup',NULL,1,NULL),(50,'16',NULL,68,'Adopt “Digital by default” principle for startup clients and vendors.',NULL,1,'ecosys',NULL,1,NULL),(51,'17',NULL,69,'Adopt a target of widespread 1 Gbps access by 2025 and roll out the networks',NULL,1,'ec',NULL,1,NULL),(52,'17',NULL,69,'Adopt a target of widespread 1 Gbps access by 2025 and roll out the networks',NULL,1,'country',NULL,1,NULL),(53,'18',NULL,70,'Develop a single crossborder commercial contract including agreed definitions on dispute resolution, consumer protection, crossborder sales and other relevant points',NULL,1,'ec',NULL,1,NULL),(54,'18',NULL,70,'Adopt and implement the single crossborder commercial contract including agreed definitions on dispute resolution, consumer protection, crossborder sales and other relevant points',NULL,1,'country',NULL,1,NULL),(55,'18',NULL,70,'Use the unified contract, once it is available',NULL,1,'startup',NULL,1,NULL),(56,'19',NULL,71,'Propose a legislative initiative to harmonise the regulatory framework on business insolvency and allow for quick bankruptcy',NULL,1,'ec',NULL,1,NULL),(57,'19',NULL,71,'Adopt the 2014 Insolvency Recommendation, which would bring much needed transparency to balance sheets and make it easy for honest entrepreneurs to get a second start',NULL,1,'country',NULL,1,NULL),(58,'19',NULL,71,'Promote second chance entrepreneurship',NULL,1,'startup',NULL,1,NULL),(59,'20',NULL,72,'Provide guidelines on criteria, procedures and fees for “startup visas,” which will remain member-state competence; create a single point of contact for all EU member states.',NULL,1,'ec',NULL,1,NULL),(60,'21',NULL,73,'Set up a simple and transparent cross-border framework for crowdfunding\n',NULL,1,'ec',NULL,1,NULL),(61,'22',NULL,73,'Coordinate the alignment of national crowdfunding policies ',NULL,1,'ec',NULL,1,NULL),(62,'20',NULL,72,'Launch and/ harmonise existing startup visa programmes to facilitate investments in startups and scale-ups',NULL,1,'country',NULL,1,NULL),(63,'21',NULL,73,'Mutually recognise crowdfunding platforms, in particular if MiFID compliant',NULL,1,'country',NULL,1,NULL),(64,'20',NULL,72,'Hire the best you can get. Keep an eye out for talent',NULL,1,'startup',NULL,1,NULL),(65,'21',NULL,73,'National startup and business communities  should use crowdfunding as an alternative financing tool and promote awareness through trainings and best-practice promotion ',NULL,1,'startup',NULL,1,NULL),(66,'23',NULL,74,'Use European Commission “bully pulpit” to encourage national tax systems to allow more effective “attraction of talent” measures',NULL,1,'ec',NULL,1,NULL),(67,'23',NULL,74,'Introduce a tax regime allowing taxation of stock options upon execution',NULL,1,'country',NULL,1,NULL),(68,'23',NULL,74,'Use a stock/share option as an effective way to attract talent and reimburse top-performing employees',NULL,1,'startup',NULL,1,NULL),(69,'24',NULL,75,'Use European Commission “bully pulpit” to encourage national tax systems to neutralize the decision of debt v. equity',NULL,1,'ec',NULL,1,NULL),(70,'24',NULL,75,'Implement notional interest deductions ',NULL,1,'country',NULL,1,NULL),(71,'24',NULL,75,'Take advantage of new measures (when they come) to make better, smarter scale-up decisions.',NULL,1,'startup',NULL,1,NULL),(72,'25',NULL,76,'Use European Commission “bully pulpit” to encourage national governments to provide incentives for angel investment',NULL,1,'ec',NULL,1,NULL),(73,'25',NULL,76,'Adopt tax incentives to promote angel investment. The private sector should be encouraged to invest in promising businesses',NULL,1,'country',NULL,1,NULL),(74,'25',NULL,76,'Encourage and inform potential investors about possible tax advantage in angel investing. ',NULL,1,'startup',NULL,1,NULL),(75,'25',NULL,76,'Angel investors should be active and engaged, providing funding and mentorship at all stages of company life',NULL,1,'ecosys',NULL,1,NULL),(76,'26',NULL,77,'Make European Fund for Strategic Investments (EFSI) permanent ',NULL,1,'ec',NULL,1,NULL),(77,'27',NULL,78,'Develop the proposal for a pan-European venture capital funds-of-funds supported by EU funds \n',NULL,1,'ec',NULL,1,NULL),(78,'28',NULL,78,'Develop multi-country funds supported by the EU budget to mobilise private capital ',NULL,1,'ec',NULL,1,NULL),(79,'29',NULL,79,'Encourage pan- European high growth stock markets ',NULL,1,'ec',NULL,1,NULL),(80,'31',NULL,80,'Monitor payments in member states and provide benchmarking services\n',NULL,1,'ec',NULL,1,NULL),(81,'32',NULL,80,'Support member states in developing prompt payment policies',NULL,1,'ec',NULL,1,NULL),(82,'29',NULL,79,'Establish dedicated high growth segments on national stock market or alternative investment markets\n',NULL,1,'country',NULL,1,NULL),(83,'30',NULL,79,'Reduce entry costs and administrative requirements to a stock exchange entry for scaleups',NULL,1,'country',NULL,1,NULL),(84,'31',NULL,80,'Improve the public payment - an average time to pay an invoice in business to government transactions to be below 30 days in the country\n',NULL,1,'country',NULL,1,NULL),(85,'32',NULL,80,'Ensure full implementation of Late Payments directive, in particular by making redress measures easier and more accessible',NULL,1,'country',NULL,1,NULL),(86,'31',NULL,80,'Pay suppliers promptly',NULL,1,'startup',NULL,1,NULL),(87,'29',NULL,79,'Banks and accelerators to educate startups and scale-ups on stock market possibilities, Provide support and help to go public',NULL,1,'ecosys',NULL,1,NULL),(88,'31',NULL,80,'Large companies should set a good example and try to reach an average time to pay an invoice in busin